targets.rawSql:
select 
total_trans_amt as average_amount_spent 
from customer_data_history_join cdhj where idstatus = 2;

with base_q as(
  select customer_age, count(*) filter (where gender = 'F' and status = 'Attrited Customer') as count_age
  from customer_data_history_join
  group by 1
)

select customer_age from base_q order by count_age desc limit 1;

with base_q as(
  select customer_age, count(*) filter (where gender = 'M' and status = 'Attrited Customer') as count_age
  from customer_data_history_join
  group by 1
)

select customer_age from base_q order by count_age desc limit 1;
